export default class RoundImage {
  static init(src) {
    return new this(src).getHttpRequestImage();
  }

  constructor(src) {
    this.info = {
      src,
    }
  }

  getHttpRequestImage = () => {
    const xhr = new XMLHttpRequest();
    xhr.onload = () => {
      const reader = new FileReader();
      reader.onloadend = () => {
        this.info.base64 = reader.result;
        console.log(this.info.base64);
      };
      reader.readAsDataURL(xhr.response);
    };
    xhr.open('GET', this.info.src);
    xhr.responseType = 'blob';
    xhr.send();
  };
}