export default class RoundImage {
  static init(src) {
    return new this(src).getDataImage();
  }

  constructor(src) {
    this.info = {
      src,
    }
  }

  getDataImage = () => {
    const cvs = document.createElement('canvas');
    const ctx = cvs.getContext("2d");
    const imageObj = new Image();
    imageObj.onload = function (e) {
      cvs.width = e.width;
      cvs.height = e.height;
      cvs.drawImage(imageObj, 0, 0);
    };
    imageObj.src = this.info.src;
    ctx.drawImage(imageObj, 0, 0);
    const theImgData = (ctx.getImageData(0, 0, cvs.width, cvs.height));
    console.log(theImgData);
  };

  getArrayBuffer = () => this.info.buffer = new ArrayBuffer(this.info.base64.length * 2)
}